mapper.select:

select * from bid



 bidType =#{bType}


  and bId = #{bId}

select *,(rate-addRate) as inRate,(combination-sumM) as enquiry,(sumM/combination*100) as schedul ,date_add(startTime, interval borrowTime month) as expire from perBid p left join (select sum(tenMoney) as sumM,bid from tender where bType = 3 GROUP BY bid)as ten on p.perBid = ten.bid
(rate-addRate) < 7 (rate-addRate) >= 7 and (rate-addRate) <8 (rate-addRate) >= 8 and (rate-addRate) <=10 (rate-addRate) > 10 and (combination-sumM) < 5000 and (combination-sumM) >= 5000 and (combination-sumM) < 10000 and (combination-sumM) >= 10000 and (combination-sumM) <= 50000 and (combination-sumM) > 50000
limit #{startT},#{tSize}
